import React, { useState, useEffect } from 'react';
import './UserPhotos.css';
import axios from 'axios';
import ImageCard from '../feed/ImageCard/ImageCard';
import BookmarkModal from '../modal/BookmarkModal';

const CollectionFeed = () => {
  const [collectionImgs, setCollectionImgs] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [photoIdBookmarked, setPhotoIdBookmarked] = useState(0);
  const [loading, setLoading] = useState(false);

  const { retrievedImgs } = props;

  useEffect(() => {
    setCollectionImgs(retrievedImgs);
    fetchCollectionImgs();
  }, [retrievedImgs]);

  const collectionImagesComponents = collectionImages.map((img) => {
    return <CollectionImage key={img.id} image_info={img} />;
  });

  return (
    <React.Fragment>
      <h2>Uploaded Images: {profileImgs.length}</h2>

      <div className="image-grid">
        {collectionImgs.map((image, index) => {
          if (image === null) {
            return null;
          }

          return (
            <ImageCard
              key={image.id}
              image={image}
              openBookmarkModal={modalIsOpen}
              setOpenBookmarkModal={setModalIsOpen}
              setPhotoId={setPhotoIdBookmarked}
            />
          );
        })}
      </div>
      <h2 style={{ textAlign: 'center' }}>{loading && 'Loading...'}</h2>
    </React.Fragment>
  );
};

export default CollectionFeed;
