    // }
    // const newImages = response.data.results;
    setImages(results);
    // console.log(response)
    // console.log(images);
  }

  //console.log(images);

  return (
    <React.Fragment>
      <Toolbar />
      <SearchBar userSubmit={onSearchSubmit} />
      <span>Found: {images.length} images</span>
      <ImageList foundImages={images} />
    </React.Fragment>
  );
}

export default MainWrapper;
