import React, { Component } from "react";

const PostPhoto = (props) => {
  return (
    <React.Fragment>
      <div>
        <img
          src={`data:image/jpg;base64,${this.props.image.img}`}
          alt={this.props.caption}
        />
      </div>
    </React.Fragment>
  );
};

export default PostPhoto;
