import React, { useState, useEffect } from "react";
import { Button, FormGroup, FormControl, FormLabel } from "react-bootstrap";
import axios from "axios";

export default function ChangePasswordPage() {

  const [email, set_email] = useState("");
  const [password, set_password] = useState("");
  const [new_password, set_new_password] = useState("");
  const [new_confirmed_password, set_new_confirmed_password] = useState("");

  function validate_email() {
    return email.length > 0 && email.length < 50;
  }

  function validate_password() {
    return password.length >= 8 && password.length < 16;
  }

  function validate_new_password() {
    return new_password.length >= 8
    && new_password.length < 16;
  }

  function validate_new_confirmed_password() {
    return new_confirmed_password.length >= 8
    && new_confirmed_password.length < 16 && new_confirmed_password==new_password;
  }

  async function attempt_password_change(event) {
    event.preventDefault();

    const response = await axios.get("http://localhost:5000/login",
    {
      params: { email: email, password: password}
    });
    console.log(response);

    response = await axios.get("http://localhost:5000/change_password",
    {
      params: { email: email, password: password, new_password: new_password }
    });
    console.log(response);

  }


  return (
        <div>
            <form onSubmit={attempt_password_change}>

              <FormGroup controlId="email" bsSize="large">
                <FormLabel>
                    Email
                </FormLabel>
                <FormControl
                  autoFocus
                  type="email"
                  value={email}
                  onChange={e => set_email(e.target.value)}
              /></FormGroup>

              <FormGroup
                  controlId="password"
                  bsSize="large">
                <FormLabel>
                  Password
                </FormLabel>

                <FormControl
                  value={password}
                  onChange={e => set_password(e.target.value)}
                  type="password"
                />
              </FormGroup>

              <FormGroup
                  controlId="new_password"
                  bsSize="large">
                <FormLabel>
                  New Password
                </FormLabel>

                <FormControl
                  value={new_password}
                  onChange={e => set_new_password(e.target.value)}
                  type="password"
                />
              </FormGroup>

              <FormGroup
                  controlId="new_confirmed_password"
                  bsSize="large">
                <FormLabel>
                  Confirm New Password
                </FormLabel>

                <FormControl
                  value={new_confirmed_password}
                  onChange={e => set_new_confirmed_password(e.target.value)}
                  type="password"
                />
              </FormGroup>

              <Button
                  block
                  bsSize="large"
                  disabled={!validate_email() || !validate_password()}
                  type="submit">

                  Login

              </Button>
            </form>
        </div>
  );



}
