import React, { useState } from "react";
import "./BookmarkModal.css";

export default function BookmarkModal({ openModal, children }) {
  if (!openModal) {
    return null;
  }

  return (
    <React.Fragment>
      <button onClick={onCloseModal}>Close Modal</button>
      {children}
    </React.Fragment>
  );
}
