import React, { useState, useEffect } from "react";
import ReactDom from "react-dom";
import "./ForgotPasswordModal";
import axios from "axios";
import { useHistory } from "react-router-dom";

import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  root: {
    "& .MuiTextField-root": {
      margin: theme.spacing(1),
      width: "ch",
    },
  },
}));

export default function ForgotPassword(props) {
  const classes = useStyles();
  const [email, set_email] = useState("");

  const history = useHistory();

  function validate_email() {
    return email.length > 0 && email.length < 50;
  }

  async function attempt_password_change(event) {
    event.preventDefault();

    var response = await axios.get(
      "http://localhost:5000/forgot_password_get_change_password_link",
      {
        params: { email: email },
      }
    );
    console.log(response);
  }

  if (!props.openForgotPasswordModal) {
    return null;
  } else {
    return ReactDom.createPortal(
      <React.Fragment>
        <div className="overlayStyles" />
        <div
          className="loginModal"
          onClick={(e) => {
            e.stopPropagation();
          }}
        >
          <form className={classes.root} noValidate autoComplete="off">
            <div className="cart-details-grid"></div>
            <h2>Email</h2>
            <div>
              <TextField
                required
                // error={errorValue}
                // helperText={errorText}
                onChange={(e) => {
                  setEmail(e.target.value);
                  //   handleTitleInput(e.target.value);
                }}
                id="outlined-required"
                label="Required"
                defaultValue={props.oldTitle}
                variant="outlined"
              />
            </div>

            <h2>Password</h2>
            <div onClick={(e) => e.stopPropagation()}>
              <TextField
                required
                // error={errorValue}
                // helperText={errorText}
                onChange={(e) => {
                  setPassword(e.target.value);
                  //   handleTitleInput(e.target.value);
                }}
                id="outlined-required"
                label="Required"
                defaultValue={props.oldTitle}
                variant="outlined"
                type="password"
              />
            </div>
            <div>
              <Button
                variant="contained"
                color="secondary"
                size="large"
                onClick={handleSendEmailClicked}
              >
                SEND EMAIL
              </Button>
            </div>
            <div>
              <Button
                variant="contained"
                color="primary"
                size="large"
