import React, { useState, useEffect } from "react";
import "./ReplyComments.css";
import axios from "axios";
import ReplyComment from "./replyComment/ReplyComment";

export default function ReplyComments(props) {
  useEffect(() => {
    fetchComments(props.location.state.id);
    console.log("update comment called");
  }, [commentUpdated, replyUpdated]);

  const getReplies = () => {
    axios({
      method: "GET",
      url: "http://localhost:5000/get_comments_to_comment",
      params: {
        comment_id: props.comment_id,
        batch_size: 10,
      },
    }).then((response) => {
      console.log(response);
    });
  };

  const replies = props.foundImages.map((img) => {
    return <ImageCard key={img.id} image={img} />;
  });

  return (
    <React.Fragment>
      <div className="reply_comments">{replies}</div>
    </React.Fragment>
  );
}
