import React, { useState, useEffect, useRef } from "react";
import "./searchbar.css";
import axios from "axios";
import Feed from "../feed/feed";
import InfiniteScroll from "react-infinite-scroll-component";

function SearchBar(props) {
  const [imgs, setImgs] = useState([]);
  const [searchVal, setSearchVal] = useState("");

  const fetchImages = (term) => {
    let cancel;
    axios({
      method: "GET",
      url: "http://localhost:5000/discovery",
      params: { query: term, batch_size: 20 }, //user_id: 1
      cancelToken: new axios.CancelToken((c) => (cancel = c)),
    })
      .then((res) => {
        setImgs([imgs.concat(res.data.result)]);
        console.log(res);
        console.log(imgs);
        console.log(term);
      })
      .catch((e) => {
        if (axios.isCancel(e)) return;
      });

    return () => cancel();
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const images = fetchImages(searchVal);
    //images.then((imageResults) => {
    //console.log(imageResults);
    // this means no images were found
    //if (imageResults.data.result !== false) {
    //setImgs(imageResults.data.result);
    //}
    //});
  };
  return (
    <React.Fragment>
      <form onSubmit={handleSubmit} className="flexContainer">
        <input
          className="inputStyle"
          type="text"
          value={searchVal}
          onChange={(event) => setSearchVal(event.target.value)}
        />
      </form>
      <Feed foundImages={imgs} fetchImages={fetchImages} query={searchVal} />
    </React.Fragment>
  );
}
export default SearchBar;
