import React, { useState, useEffect } from 'react';
import TextField from '@material-ui/core/TextField';

export default function FirstName(props) {
  const [text, setText] = useState('');
  const [errorText, setErrorText] = useState('');
  const [errorValue, setErrorValue] = useState(false);
  const [firstRender, setFirstRender] = useState(true);

  const { submitFormClicked } = props;

  useEffect(() => {
    setFirstRender(false);
  }, []);

  useEffect(() => {
    if (firstRender === false) {
      const result = handleTagsText(text);
      props.setTagsValidated([result, text]);
    }
  }, [submitFormClicked]);

  const handleTagsText = (text) => {
    if (text.length > 0) {
      setErrorValue(false);
      setErrorText('');
      return true;
    } else {
      setErrorText('First name must be included');
      setErrorValue(true);
      return false;
    }
  };
  return (
    <React.Fragment>
      <h1></h1>
      <TextField
        error={errorValue}
        helperText={errorText}
        onChange={(e) => {
          setText(e.target.value);
          handleTagsText(e.target.value);
        }}
        label="Tags - Must be comma seperated (e.g. 'nature','trees','green')"
        InputLabelProps={{
          shrink: true,
        }}
      />
    </React.Fragment>
  );
}
