import React, { useState, useEffect } from 'react';

import MainPage from './MainPage';
import LoginPage from './LoginPage';
import RegistrationPage from './RegistrationPage';
import ProfilePage from './ProfilePage';

import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from 'react-router-dom';

function App() {
  const [loggedIn, setLoggedIn] = useState(false);

  console.log('RENDERED => ' + loggedIn);

  const updateLoginStatus = (status) => {
    console.log('STATUS ' + status);
    setLoggedIn(status);
    localStorage.setItem('userLoggedIn', status);
  };

  useEffect(() => {
    console.log('use effect');
    const userLoggedIn = localStorage.getItem('userLoggedIn');
    console.log('userloggedin ' + userLoggedIn);
    if (userLoggedIn) {
      setLoggedIn(true);
      console.log('set to true with ' + userLoggedIn);
    }
  }, [loggedIn]);

  return (
    <Router>
      <React.Fragment>
        <Switch>
          <Route path="/" exact>
            <MainPage updateLoginStatus={updateLoginStatus} />
          </Route>
          <Route path="/login">
            {loggedIn ? (
              <Redirect to="/" />
            ) : (
              <LoginPage loggedIn={updateLoginStatus} />
            )}
          </Route>
          <Route path="/signup">
            {loggedIn ? <Redirect to="/" /> : <RegistrationPage />}
          </Route>
        </Switch>
        <Route path="/profile" component={ProfilePage} />
      </React.Fragment>
    </Router>
  );
}

export default App;
