import React, { useState, useEffect } from 'react';
import ReactDom from 'react-dom';
import './SignupModal.css';
import { Button, FormGroup, FormControl, FormLabel } from 'react-bootstrap';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import Email from './Textfields/Email';
import FirstName from './Textfields/FirstName';
import LastName from './Textfields/Lastname';
import Password from './Textfields/Password';
import Username from './Textfields/Username';

export default function RegistrationPage(props) {
  const [submitFormClicked, setSubmitFormClicked] = useState(false);
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');

  const [firstNameValidated, setFirstNameValidated] = useState(false);
  const [lastNameValidated, setLastNamedValidated] = useState(false);
  const [emailValidated, setEmailValidated] = useState(false);
  const [passwordValidated, setPasswordValidated] = useState(false);
  const [usernameValidated, setUsernameValidated] = useState(false);
  const history = useHistory();

  function validate_email() {
    return email.length > 0 && email.length < 50;
  }

  function validate_password() {
    return password.length >= 8 && password.length < 16;
  }

  function validate_first_name() {
    return email.length > 0 && email.length < 50;
  }

  function validate_last_name() {
    return password.length > 0 && password.length < 50;
  }

  function validate_username() {
    return password.length > 0 && password.length < 32;
  }

  useEffect(() => {
    const registerUser = () => {
      axios({
        method: 'POST',
        url: 'http://localhost:5000/create_user',
        params: {
          email: email,
          password: password,
          first: firstName,
          last: lastName,
          username: username,
        },
      }).then((response) => {
        console.log(response);
        if (response.data.result) {
        }
      });
    };

    if (
      firstNameValidated &&
      lastNameValidated &&
      emailValidated &&
      passwordValidated &&
      usernameValidated
    ) {
      registerUser();
    }
  }, [
    firstNameValidated,
    lastNameValidated,
    emailValidated,
    passwordValidated,
    usernameValidated,
  ]);

  async function attempt_registration(event) {
    event.preventDefault();

    const response = await axios.get('http://localhost:5000/create_user', {
      params: {
        email: email,
        password: password,
        first: first_name,
        last: last_name,
        username: username,
      },
    });
    console.log(response);

    if (response.data.result !== false) {
      history.push('/');
      history.go(0);
    }
  }

  console.log(props.openSignupModal);

  if (!props.openSignupModal) {
    return null;
  } else {
    return ReactDom.createPortal(
      <React.Fragment>
        <div className="overlayStyles" />
        <div
          className="signupModal"
          onClick={(e) => {
            e.stopPropagation();
          }}
        >
          <h1>Join PhotoPro</h1>

          <form onSubmit={attempt_registration}>
            <FirstName
              submitFormClicked={submitFormClicked}
              setCardNumValidated={setCardNumValidated}
            />
            <LastName
              submitFormClicked={submitFormClicked}
              setCardNameValidated={setCardNameValidated}
            />
            <Email
              submitFormClicked={submitFormClicked}
              setCardMonthValidated={setCardMonthValidated}
            />
            <Username
              placeOrderClicked={submitFormClicked}
              setCardYearValidated={setCardYearValidated}
            />
            <Password
              submitFormClicked={submitFormClicked}
              setCardCvvValidated={setCardCvvValidated}
            />

            <div className="register-button">
              <Button
                block
                bsSize="large"
                onClick={() => {
                  setSubmitFormClicked(true);
                }}
              >
                Register
              </Button>
            </div>
          </form>
        </div>
      </React.Fragment>,
      document.getElementById('toolbarPortal')
    );
  }
}
