import React, { useState } from "react";
import "./searchbar.css";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";

const useStyles = makeStyles((theme) => ({
  root: {
    margin: theme.spacing(1),
    width: "100ch",
    backgroundColor: "white",
  },
}));

function SearchBarInput(props) {
  const [searchVal, setSearchVal] = useState("");
  const classes = useStyles();

  const handleSubmit = (e) => {
    e.preventDefault();
    setTimeout(() => {
      if ((searchVal.match(/[/[\s]/g) || []).length === searchVal.length) {
        props.setQuery(null);
      } else {
        props.setQuery(searchVal);
      }
    }, 100);
  };

  let backgroundImages = [];
  let index = 0;

  backgroundImages[0] = img1;
  backgroundImages[1] = img2;
  backgroundImages[2] = img3;
  backgroundImages[3] = img4;
  backgroundImages[4] = img5;
  backgroundImages[5] = img6;
  backgroundImages[6] = img7;
  index = Math.floor(Math.random() * backgroundImages.length);
  console.log(index);

  return (
    <React.Fragment>
      <div className="bar">
        <div className="searchBar">
          <form
            onSubmit={handleSubmit}
            className={classes.root}
            noValidate
            autoComplete="off"
          >
            <TextField
              className={classes.text}
              id="outlined-basic"
              label="Search for stock photos"
              variant="outlined"
              size="small"
              fullWidth
              value={searchVal}
              onChange={(event) => setSearchVal(event.target.value)}
            />
          </form>
        </div>
      </div>
    </React.Fragment>
  );
}

export default SearchBarInput;
