import React, { useState, useEffect } from 'react';
import axios from 'axios';

const UserPhotos = () => {
  const [profileImgs, setProfileImgs] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [photoIdBookmarked, setPhotoIdBookmarked] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const [loading, setLoading] = useState(false);
  const observer = useRef();
  const lastImageRef = useCallback(
    (node) => {
      if (loading) return;
      if (observer.current) observer.current.disconnect();
      observer.current = new IntersectionObserver((entries) => {
        if (entries[0].isIntersecting && hasMore) {
          fetchImages(props.query);
        }
      });

      if (node) observer.current.observe(node);
    },
    [loading, hasMore]
  );

  useEffect(() => {
    setProfileImgs([]);
    setHasMore(false);
    fetchImages(props.query);
  }, [props.query]);

  const fetchImages = (term) => {
    setLoading(true);

    axios({
      method: 'GET',
      url: 'http://localhost:5000/profile_photos',
      params: { batch_size: 30 }, //user_id: 1
    }).then((res) => {
      console.log(res);
      if (res.data.result != false) {
        setHasMore(true);
        setTimeout(() => {
          setLoading(false);
          setProfileImgs((prevImgs) => {
            return [...prevImgs, ...res.data.result];
          });
        }, 500);
      } else {
        console.log('no more images to return');
        setLoading(false);
        setHasMore(false);
      }
    });
  };

  console.log(`LENGTH = ${imgs.length}`);
  console.log(`HASMORE = ${hasMore}`);
  console.log(`LOADING = ${loading}`);

  return (
    <React.Fragment>
      <h2>Found Images: {imgs.length}</h2>

      <div className="image-grid">
        {profileImgs.map((image, index) => {
          if (image === null) {
            return null;
          }

          if (profileImgs.length === index + 1) {
            return (
              <React.Fragment key={index}>
                <ImageCard
                  key={image.id}
                  image={image}
                  openBookmarkModal={modalIsOpen}
                  setOpenBookmarkModal={setModalIsOpen}
                  setPhotoId={setPhotoIdBookmarked}
                />
                <div
                  key={index}
                  ref={lastImageRef}
                  style={{
                    position: 'relative',
                    bottom: '200px',
                    // border: '3px solid red',
                    height: '0%',
                  }}
                ></div>
              </React.Fragment>
            );
          } else {
            return (
              <ImageCard
                key={image.id}
                image={image}
                openBookmarkModal={modalIsOpen}
                setOpenBookmarkModal={setModalIsOpen}
                setPhotoId={setPhotoIdBookmarked}
              />
            );
          }
        })}
      </div>
      <h2 style={{ textAlign: 'center' }}>{loading && 'Loading...'}</h2>
      <h2 style={{ textAlign: 'center' }}>
        {!hasMore && 'No more images to display'}
      </h2>

      <BookmarkModal
        openModal={modalIsOpen}
        onCloseModal={() => setModalIsOpen(false)}
        photoId={photoIdBookmarked}
      ></BookmarkModal>
    </React.Fragment>
  );
};

export default Feed;
