import React, { useState } from "react";
import "./searchbar.css";
import { makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";

import img1 from "../../background/1.jpg";
import img2 from "../../background/1.jpg";
import img3 from "../../background/1.jpg";
import img4 from "../../background/1.jpg";
import img5 from "../../background/1.jpg";

const useStyles = makeStyles((theme) => ({
  root: {
    margin: theme.spacing(1),
    width: "100ch",
  },
}));

function SearchBarInput(props) {
  const [searchVal, setSearchVal] = useState("");
  const classes = useStyles();

  const handleSubmit = (e) => {
    e.preventDefault();
    setTimeout(() => {
      if ((searchVal.match(/[/[\s]/g) || []).length === searchVal.length) {
        props.setQuery(null);
      } else {
        props.setQuery(searchVal);
      }
    }, 100);
  };

  const backgroundImages = [];
  const index = 0;

  backgroundImages[0] = (
    <img className="background-image" src={img1} alt="background" />
  );
  backgroundImages[1] = (
    <img className="background-image" src={img1} alt="background" />
  );
  backgroundImages[2] = (
    <img className="background-image" src={img1} alt="background" />
  );
  backgroundImages[3] = (
    <img className="background-image" src={img1} alt="background" />
  );
  backgroundImages[4] = (
    <img className="background-image" src={img1} alt="background" />
  );
  index = Math.floor(Math.random() * backgroundImages.length);
  console.log(index);

  return (
    <React.Fragment>
      <div className="bar">
        <div className="searchBar">
          <form
            onSubmit={handleSubmit}
            className={classes.root}
            noValidate
            autoComplete="off"
          >
            <TextField
              className={classes.text}
              id="outlined-basic"
              label="Search for stock photos"
              variant="outlined"
              size="small"
              fullWidth
              value={searchVal}
              onChange={(event) => setSearchVal(event.target.value)}
            />
          </form>
        </div>
      </div>
    </React.Fragment>
  );
}

export default SearchBarInput;
