import React, { useState } from 'react';
import ReactDom from 'react-dom';
import './BookmarkConfirmationModal.css';
import axios from 'axios';

export default function BookmarkConfirmationModal(props) {
  const [photoTitle, setPhotoTitle] = useState(props.photoID);
  const [collectionName, setCollectionName] = useState(
    props.modalCollectionName
  );

  if (collectionName === null) {
    setCollectionName('the chosen collection');
  }

  console.log('IN HERE');

  axios({
    url: 'http://localhost:5000/get_post_title',
    params: { photo_id: props.photoID },
  }).then((response) => {
    if (response.data.result) {
      setPhotoTitle(response.data.result);
    }
  });

  if (!props.openModal) {
    return null;
  } else {
    return ReactDom.createPortal(
      <React.Fragment>
        <div className="bookmark-confirmation-overlay" />
        <div
          className="bookmark-confirmation-modal"
          onClick={(e) => {
            e.stopPropagation();
          }}
        >
          <h1>
            '{photoTitle}' has been successfully added to collection '
            {collectionName}`
          </h1>
        </div>
      </React.Fragment>,
      document.getElementById('confirmationPortal2')
    );
  }
}
