import React, { useState, useEffect } from "react";
import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import axios from "axios";

export default function LoginPage() {

  const [email, set_email] = useState("");
  const [password, set_password] = useState("");

    function validate_email() {
      return email.length > 0 && email.length < 50;
    }

    function validate_password() {
      return password.length > 8 && password.length < 16;
    }

    function attempt_login(event) {
      event.preventDefault();
    }

  // initialise the array to an empty array []

//  async function onSearchSubmit(term) {
//    const response = await axios.get("https://api.unsplash.com/search/photos", {
//      params: { query: term },
//      headers: {
//        Authorization: "Client-ID d59bDVT3fM5DB90WT41RUwaYFzikaPGfpviDpMqMTls",
//      },
//    });
//
//    //setImages(response.data.results.map((x) => images.push(x)));
//    const newImages = response.data.results;
//    setImages(newImages);
//    // console.log(images);
//  }



  return (
        <div>
            <form onSubmit={attempt_login}>
              <FormGroup controlId="email" bsSize="large">
                <ControlLabel>
                    Email
                </ControlLabel>
                <FormControl
                  autoFocus
                  type="email"
                  value={email}
                  onChange={e => set_email(e.target.value)}
                /></FormGroup>
                <FormGroup
                    controlId="password"
                    bsSize="large">
                <ControlLabel>
                  Password
                </ControlLabel>
                <FormControl
                  value={password}
                  onChange={e => set_password(e.target.value)}
                  type="password"
                />
              </FormGroup>
              <Button
                  block
                  bsSize="large"
                  disabled={!validate_email() || !validate_password()}
                  type="submit">

                  Login

              </Button>
            </form>
        </div>
  );



}
