import React, { useState, useEffect } from "react";
import axios from "axios";
import ReactDom from "react-dom";
import "./DeleteAccountModal.css";
import Button from "@material-ui/core/Button";
import { useHistory } from "react-router-dom";

export default function DeleteAccountModal(props) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showButtons, setShowButtons] = useState(true);

  const history = useHistory();

  const handleDeleteAccountClicked = () => {
    axios({
      method: "POST",
      url: "http://localhost:5000/delete_user",
      params: {
        email: email,
        password: password,
      },
    }).then((response) => {
      console.log(response);
      if (response.data.result !== false) {
        console.log(response);
      }
    });
  };

  const handleDeleteButtonClicked = () => {
    console.log("deleteButtonClicked");
    setShowButtons(false);
  };

  if (!props.openDeleteAccountModal) {
    return null;
  } else {
    return ReactDom.createPortal(
      <React.Fragment>
        <div className="overlay-styles" />
        <div
          className="confirmation-modal"
          onClick={(e) => e.stopPropagation()}
        >
          <h1>Are you sure you want to delete your account?</h1>
          <h3>
            Deleting your account is permanent and will remove all content
            including uploaded and purchased photos, collections and comments.
          </h3>

          {showButtons ? (
            <React.Fragment>
              <Button
                variant="contained"
                color="secondary"
                onClick={handleDeleteButtonClicked}
              >
                Yes, delete
              </Button>
              <Button
                variant="contained"
                color="primary"
                onClick={() => {
                  props.setOpenDeleteAccountModal(false);
                }}
              >
                No, don't delete
              </Button>
            </React.Fragment>
          ) : null}

          {showButtons ? (
            <React.Fragment>
              <Button
                variant="contained"
                color="secondary"
                onClick={handleDeleteButtonClicked}
              >
                Yes, delete
              </Button>
              <Button
                variant="contained"
                color="primary"
                onClick={() => {
                  props.setOpenDeleteAccountModal(false);
                }}
              >
                No, don't delete
              </Button>
            </React.Fragment>
          ) : null}
        </div>
      </React.Fragment>,
      document.getElementById("toolbarPortal")
    );
  }
}
