import React, { useState, useEffect } from "react";
import axios from "axios";
import Toolbar from "./toolbar/toolbar";
import SearchBar from "./SearchBar/SearchBar";
import ImageList from "./ImageList/ImageList";

function MainWrapper() {
  const [images, setImages] = useState([]);

  async function onSearchSubmit(term) {
    //console.log(term);

    const response = await axios.get("https://api.unsplash.com/search/photos", {
      params: { query: term },
      headers: {
        Authorization: "Client-ID YFIXmrIAVXjYUcg-E0zPT4jn0g25CPdcT2lEzabh9y4",
      },
    });
    console.log(response);

    setImages(response.data.results.map((x) => images.push(x)));

    //console.log(response.data.results);
    console.log(images.length);
  }

  onSearchSubmit(term).then(() => {
    console.log(images);
  });

  return (
    <React.Fragment>
      <Toolbar />
      <SearchBar userSubmit={onSearchSubmit} />
      <span>Found: {images.length} images</span>
      <ImageList foundImages={images} />
    </React.Fragment>
  );
}

export default MainWrapper;
