      setLoggedIn(true);
    }
  }, []);

  return (
    <Router>
      <React.Fragment>
        <Switch>
          <Route path="/" exact component={MainPage} />
          <Route path="/login" component={LoginPage} />
          <Route path="/signup" component={RegistrationPage} />
        </Switch>
      </React.Fragment>
    </Router>
  );
}

export default App;
