import React, { useState } from 'react';
import Comment from './comment/Comment';
import './Comments.css';
import axios from 'axios';

export default function Comments(props) {
  const [comment_input, set_comment_input] = useState('');
  const [comments, setComments] = useState([]);

  useEffect(() => {
    fetchComments(props.location.state.id);
  }, [comments]);

  const fetchComments = (id) => {
    axios({
      method: 'GET',
      url: 'http://localhost:5000/get_comments_to_image',
      params: { image_id: id, batch_size: 20 },
    }).then((res) => {
      if (res.data.result != false) {
        setComments(res.data.result);
      }
    });
  };

  const commentsList = props.comments_list.map((comment) => {
    return <Comment comment_info={comment} />;
  });

  const handlePostClick = (e) => {
    e.preventDefault();
    postComments(comment_input);
  };

  const postComments = (comment_input) => {
    axios({
      method: 'POST',
      url: 'http://localhost:5000/post_comment_to_image',
      params: { comment: comment_input, image_id: props.image_id },
    }).then((res) => {
      setComments(props.comments_list.concat(comment_input));
    });
  };

  return (
    <React.Fragment>
      <div className="comment-input">
        <form onSubmit={handlePostClick}>
          <div>
            <input
              type="comment"
              id="comment_input"
              value={comment_input}
              onChange={(e) => set_comment_input(e.target.value)}
            />
          </div>
        </form>
      </div>
      <div className="comments-container">{commentsList}</div>
    </React.Fragment>
  );
}
