import React, { useState } from 'react';
import './toolbar.css';
import { useHistory } from 'react-router-dom';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    margin: theme.spacing(1),
    border: '3px solid red',
  },
}));

function Toolbar() {
  const history = useHistory();
  const classes = useStyles();
  const loggedIn = localStorage.getItem('userLoggedIn');

  const [anchorEl, setAnchorEl] = useeState(null);

  const handleAccountClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleAccountClose = () => {
    setAnchorEl(null);
  };

  const handleSignInClicked = () => {
    history.push('/login');
  };

  const handleSignUpClicked = () => {
    history.push('/signup');
  };

  const handleProfileClicked = function () {
    history.push('/profile/1');
  };

  const handleLogoutClicked = () => {
    localStorage.clear();
    history.push('/');
    history.go(0); // forces the page to re-render if you are already on it which causes it to display the right information
  };

  const handleDiscoveryClicked = () => {
    history.push('/');
  };

  const handleUploadClicked = () => {
    history.push('/uploadphoto');
  };

  const handleChangePassClicked = () => {
    history.push('/changepassword');
  };

  let buttons;
  if (loggedIn === 'true') {
    buttons = (
      <React.Fragment>
        <div className={'flex-container-buttons-2'}>
          <Button size="small" onClick={handleDiscoveryClicked}>
            Discovery
          </Button>
          <Button size="small">Collections</Button>

          <Button
            aria-controls="simple-menu"
            aria-haspopup="true"
            onClick={handleClick}
          >
            Open Menu
          </Button>
          <Menu
            id="simple-menu"
            anchorEl={anchorEl}
            keepMounted
            open={Boolean(anchorEl)}
            onClose={handleClose}
          >
            <MenuItem onClick={handleClose}>Profile</MenuItem>
            <MenuItem onClick={handleClose}>My account</MenuItem>
            <MenuItem onClick={handleClose}>Logout</MenuItem>
          </Menu>
          <Button size="small" onClick={handleProfileClicked}>
            Profile
          </Button>

          <Button size="small" onClick={handleChangePassClicked}>
            Change Password
          </Button>
          <Button size="small" onClick={handleLogoutClicked}>
            Log Out
          </Button>
        </div>
        <div className="toolbar-left">
          <button onClick={handleUploadClicked}>Upload Photo</button>
        </div>
      </React.Fragment>
    );
  } else {
    buttons = (
      <React.Fragment>
        <div className="toolbar-left-placeholder"></div>
        <div className="flex-container-buttons-1">
          <Button style={{ color: 'white' }} onClick={handleSignInClicked}>
            Sign in
          </Button>
          <Button style={{ color: 'white' }} onClick={handleSignUpClicked}>
            Getting Started
          </Button>
        </div>
      </React.Fragment>
    );
  }

  return (
    <React.Fragment>
      <div className="flex-container-toolbar">
        <h1 className="toolbar-text">PhotoPro</h1>
        {buttons}
      </div>
    </React.Fragment>
  );
}

export default Toolbar;
