import React, { useState, useEffect } from "react";
import "./Collection.css";
import { Redirect } from "react-router-dom";

export default function Collection(props) {
  console.log(props);
  const [collectionClicked, setCollectionClicked] = useState(false);

  const handleCollectionClicked = () => {
    console.log("collection clicked!");
    setCollectionClicked(true);
  };

  const collectionData = (
    <Redirect
      push
      to={{
        pathname: `/collection-${props.collection_id}`,
        state: {
          collection_id: `${props.collection_id}`,
        },
      }}
    />
  );

  let componentsRender;
  if (collectionClicked) {
    componentsRender = collectionClicked;
  } else {
    componentsRender = (
      <div className="collection-box" onClick={handleCollectionClicked}>
        {props.collection_name}
      </div>
    );
  }

  return <React.Fragment>{componentsRender}</React.Fragment>;
}
