import React, { useState, useEffect } from "react";
import ImageUploader from "react-images-upload";
import axios from "axios";
import Button from "@material-ui/core/Button";
import { useHistory } from "react-router-dom";

export default function UploadProfilePage(props) {
  const [img, setImg] = useState(null);
  const [pictures, setPictures] = useState([]);

  const history = useHistory();

  const onDrop = (pictureFiles, pictureDataURLs) => {
    setPictures(pictures.concat(pictureFiles));
    setImg(pictureDataURLs[0]);
  };

  console.log(img);

  const postProfilePhoto = () => {
    axios({
      method: "POST",
      url: "http://localhost:5000/post_profile_photo",
      params: {
        image: img,
      },
    }).then((response) => {
      console.log(response);
    });
  };

  const handleSaveProfileClicked = () => {
    postProfilePhoto();
  };

  return (
    <React.Fragment>
      <div className="upload-image-wrapper">
        <ImageUploader
          withIcon={true}
          buttonText="Choose image"
          onChange={onDrop}
          imgExtension={[".jpg", ".png", ".jpeg"]}
          maxFileSize={5242880}
          withPreview={true}
          singleImage={true}
          withLabel={true}
        />
        <Button
          variant="contained"
          color="primary"
          size="large"
          onClick={handleSaveProfileClicked}
        >
          Save Profile Photo
        </Button>
        <Button
          variant="contained"
          color="primary"
          size="large"
          onClick={() => {
            history.goBack();
          }}
        >
          Cancel
        </Button>
      </div>
    </React.Fragment>
  );
}
