import React, { useState, useEffect, useRef } from 'react';
import './feed.css';
import axios from 'axios';
import ImageCard from './ImageCard/ImageCard';
import BookmarkModal from '../modal/BookmarkModal';
import InfiniteScroll from 'react-infinite-scroll-component';
import { ImageSearch } from '@material-ui/icons';

const fetchImages = (term, setImgs, setHasMore, setLoading) => {
  setLoading(true);

  axios({
    method: 'GET',
    url: 'http://localhost:5000/discovery',
    params: { query: term, batch_size: 20 }, //user_id: 1
  }).then((res) => {
    if (res.data.result != false) {
      setHasMore(true);
      setTimeout(() => {
        setLoading(false);
        setImgs((prevImgs) => {
          return [...prevImgs, ...res.data.result];
        });
      }, 500);

      // setImgs(imgs.concat(res.data.result));
    } else {
      console.log('no more images to return');
      setLoading(false);
      setHasMore(false);
      // setImgs((prevImgs) => {
      //   return [...prevImgs];
      // });
    }
  });
};

const Feed = (props) => {
  const [imgs, setImgs] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [photoIdBookmarked, setPhotoIdBookmarked] = useState(0);
  const [hasMore, setHasMore] = useState(true);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setImgs([]);
    setHasMore(false);
  }, [props.query]);

  fetchImages(props.query, setImgs, setHasMore, setLoading);

  console.log(`LENGTH = ${imgs.length}`);
  console.log(`HASMORE = ${hasMore}`);

  let images = null;
  if (imgs != null) {
    images = imgs.map((img) => {
      return (
        <ImageCard
          key={img.id}
          image={img}
          openBookmarkModal={modalIsOpen}
          setOpenBookmarkModal={setModalIsOpen}
          setPhotoId={setPhotoIdBookmarked}
        />
      );
    });
  }

  return (
    <React.Fragment>
      <h2>Found Images: {imgs.length}</h2>

      <h2>{loading && 'Loading...'}</h2>

      <div className="image-list">{images}</div>

      <BookmarkModal
        openModal={modalIsOpen}
        onCloseModal={() => setModalIsOpen(false)}
        photoId={photoIdBookmarked}
      ></BookmarkModal>
    </React.Fragment>
  );
};

export default Feed;
