import React from "react";
import Button from "@material-ui/core/Button";
import "./ShoppingItem.css";

export default function ShoppingItem(props) {
  const apiAddPurchase = (imageID) => {
    axios({
      method: "POST",
      url: "http://localhost:5000/add_purchase",
      params: {
        save_for_later: 0,
        purchased: 0,
        image_id: String(imageID),
      },
    }).then((response) => {
      if (response.data.result !== false) {
        props.setSaveForLaterButtonClicked(true);
        console.log(response);
      }
    });
  };

  const handSaveLaterButton = () => {
    apiAddPurchase();
  };

  const handleDeleteButton = () => {};

  return (
    <React.Fragment>
      <div className="shopping-item">
        <div className="shopping-item-grid">
          <div className="image">
            <img
              src={`data:image/jpg;base64,${props.img}`}
              alt={props.caption}
            />
          </div>
          <div className="image-info">
            <h2>Title: {props.title}</h2>
            <h2>Caption:</h2>
            <p>{props.caption}</p>
            <h3>Price: {props.price}</h3>
            <Button
              variant="outlined"
              color="primary"
              onClick={handSaveLaterButton}
            >
              Save for later
            </Button>
            <Button
              variant="outlined"
              color="primary"
              onClick={handleDeleteButton}
            >
              Delete
            </Button>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
}
